package com.programe.datastructure.assignments.nov12;

public class AmazingSubarrays {
    public static void main(String[] args) {
        /**
         * Q3. Amazing Subarrays
         * You are given a string S, and you have to find all the amazing substrings of S.
         * An amazing Substring is one that starts with a vowel (a, e, i, o, u, A, E, I, O, U).
         * Input
         *
         * Input
         * Only argument given is string S.
         * Output - Return a single integer X mod 10003, here X is the number of Amazing Substrings in given the string.
         *
         * Constraints
         * 1 <= length(S) <= 1e6
         * S can have special characters
         *
         * Example
         *
         * Input
         *     ABEC
         *
         * Output
         *     6
         * Explanation
         *     Amazing substrings of given string are :
         *     1. A
         *     2. AB
         *     3. ABE
         *     4. ABEC
         *     5. E
         *     6. EC
         *     here number of substrings are 6 and 6 % 10003 = 6.
         */
        String str = "";
       // String str="aaaaaaaaaa";
        System.out.print("\nFormula Expacted - "+((str.length()*(str.length()+1))/2)%10003);
        System.out.print("\nCount of special sub-string - "+solve(str));

    }

    public static int solve(String str) {

        long count=0;
        long n = str.length();
        for(int i=0;i<n;i++) {
            if(str.charAt(i)=='a' || str.charAt(i)=='A' ||
                    str.charAt(i)=='e' || str.charAt(i)=='E' ||
                    str.charAt(i)=='i' || str.charAt(i)=='I' ||
                    str.charAt(i)=='o' || str.charAt(i)=='O' ||
                    str.charAt(i)=='u' || str.charAt(i)=='U') {
                count += n-i;
                count %=10003;
            }
        }
        return (int)count;
    }
}
